import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function AddStudent() {
  const [student, setStudent] = useState({ name: '', email: '', course: '', enrollmentDate: '' });
  const navigate = useNavigate();

  
  const handleBack = (e) => {
    e.preventDefault();
    axios.post('http://localhost:8080/students', student)
      .then(() => navigate('/'));
  };

  return (
    <div className="container mt-5">
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit}>
        <input className="form-control mb-2" placeholder="Name" value={student.name}
               onChange={e => setStudent({ ...student, name: e.target.value })} />
        <input className="form-control mb-2" placeholder="Email" value={student.email}
               onChange={e => setStudent({ ...student, email: e.target.value })} />
        <input className="form-control mb-2" placeholder="Course" value={student.course}
               onChange={e => setStudent({ ...student, course: e.target.value })} />
        <input type="date" className="form-control mb-2" value={student.enrollmentDate}
               onChange={e => setStudent({ ...student, enrollmentDate: e.target.value })} />
        <button className="btn btn-success">Submit</button>
      </form>
    </div>
  );
}

export default AddStudent;
