import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditStudent() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [student, setStudent] = useState({
    name: '',
    email: '',
    course: '',
    enrollmentDate: ''
  });

  // Fetch student data by ID
  useEffect(() => {
    axios.get(`http://localhost:8080/students/${id}`)
      .then(res => {
        const s = res.data;
        // Format enrollmentDate for input[type="date"]
        const formattedDate = s.enrollmentDate ? s.enrollmentDate.substring(0, 10) : '';
        setStudent({
          ...s,
          enrollmentDate: formattedDate
        });
      })
      .catch(err => console.error('Failed to fetch student:', err));
  }, [id]);

  // Handle input changes
  const handleChange = (e) => {
    setStudent({
      ...student,
      [e.target.name]: e.target.value
    });
  };

  
  // Submit update
  const handleSubmit = (e) => {

    e.preventDefault();
    // Inside EditStudent, add similar validation logic before axios.put()
// Example in handleSubmit:

const validate = () => {
  const errs = {};
  if (!student.name.trim()) errs.name = "Name is required";
  if (!student.email.trim()) errs.email = "Email is required";
  else if (!/\S+@\S+\.\S+/.test(student.email)) errs.email = "Email is invalid";
  if (!student.course.trim()) errs.course = "Course is required";
  if (!student.enrollmentDate) errs.enrollmentDate = "Enrollment Date is required";

  setErrors(errs);
  return Object.keys(errs).length === 0;
};

const handleSubmit = (e) => {
  e.preventDefault();

  if (!validate()) return;

  axios.put(`http://localhost:8080/students/${id}`, student)
    .then(() => navigate('/'))
    .catch(err => {
      if (err.response && err.response.status === 400) {
        setErrors(err.response.data);
      } else {
        console.error(err);
      }
    });
};

    axios.put(`http://localhost:8080/students/${id}`, student)
      .then(() => navigate("/"))
      .catch(err => console.error("Update failed:", err));
  };

  return (
    <div className="container mt-5">
      <h2>Edit Student</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group mb-3">
          <label>Name</label>
          <input type="text" name="name" value={student.name} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group mb-3">
          <label>Email</label>
          <input type="email" name="email" value={student.email} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group mb-3">
          <label>Course</label>
          <input type="text" name="course" value={student.course} onChange={handleChange} className="form-control" required />
        </div>
        <div className="form-group mb-3">
          <label>Enrollment Date</label>
          <input type="date" name="enrollmentDate" value={student.enrollmentDate} onChange={handleChange} className="form-control" required />
        </div>
        <button type="submit" className="btn btn-primary">Update</button>
      </form>
    </div>
  );
}

export default EditStudent;
